query:
-- Limpar produtos dos clientes
UPDATE clientes SET produtoPrincipal = NULL WHERE projectId = 1;

-- Deletar associações clientes-mercados
DELETE FROM clientes_mercados WHERE clienteId IN (SELECT id FROM clientes WHERE projectId = 1);

-- Deletar mercados únicos
DELETE FROM mercados_unicos WHERE projectId = 1;

-- Deletar concorrentes
DELETE FROM concorrentes WHERE projectId = 1;

-- Deletar leads
DELETE FROM leads WHERE projectId = 1;

-- Verificar limpeza
SELECT 
  (SELECT COUNT(*) FROM clientes WHERE projectId = 1) as clientes_restantes,
  (SELECT COUNT(*) FROM mercados_unicos WHERE projectId = 1) as mercados_deletados,
  (SELECT COUNT(*) FROM concorrentes WHERE projectId = 1) as concorrentes_deletados,
  (SELECT COUNT(*) FROM leads WHERE projectId = 1) as leads_deletados;

